import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../../context/AuthContext";
import { getBookById, editBook } from "../../api/books";

const EditBook = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { user, authHeader } = useAuth();
    const [book, setBook] = useState({
        title: "",
        author: "",
        description: "",
    });
    const [loading, setLoading] = useState(false);
    const [fetching, setFetching] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchBook = async () => {
            if (!user || !authHeader) {
                setError("Please login to edit books");
                setFetching(false);
                return;
            }

            try {
                const base64Credentials = authHeader.replace("Basic ", "");
                const credentials = atob(base64Credentials);
                const [email, password] = credentials.split(":");

                const data = await getBookById(id, email, password);
                setBook(data);
            } catch (err) {
                setError("Failed to load book");
                console.error(err);
            } finally {
                setFetching(false);
            }
        };

        fetchBook();
    }, [id, user, authHeader]);

    const submit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        if (!user || !authHeader) {
            setError("Please login to edit books");
            setLoading(false);
            return;
        }

        try {
            const base64Credentials = authHeader.replace("Basic ", "");
            const credentials = atob(base64Credentials);
            const [email, password] = credentials.split(":");

            const userId = user.id || user.userId;
            if (!userId) {
                setError("User ID not found. Please login again.");
                setLoading(false);
                return;
            }

            await editBook(id, userId, book, email, password);
            navigate("/admin");
        } catch (err) {
            setError("Failed to update book. Please try again.");
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    return (
        <form onSubmit={submit}>
            <h2>Edit Book</h2>
            <input value={book.title || ""} onChange={e => setBook({...book, title:e.target.value})}/>
            <input value={book.author || ""} onChange={e => setBook({...book, author:e.target.value})}/>
            <textarea value={book.description || ""} onChange={e => setBook({...book, description:e.target.value})}/>
            <button>Update</button>
        </form>
    );
};

export default EditBook;
