import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../../context/AuthContext";
import { getBookById } from "../../api/books";
import { motion } from "framer-motion";

const BookDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { user, authHeader } = useAuth();
    const [book, setBook] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchBook = async () => {
            if (!user || !authHeader) {
                setError("Please login to view book details");
                setLoading(false);
                return;
            }

            try {
                // Extract email and password from authHeader (Basic base64(email:password))
                const base64Credentials = authHeader.replace("Basic ", "");
                const credentials = atob(base64Credentials);
                const [email, password] = credentials.split(":");

                const data = await getBookById(id, email, password);
                setBook(data);
            } catch (err) {
                setError("Failed to load book details");
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchBook();
    }, [id, user, authHeader]);

    if (loading) {
        return (
            <div style={styles.container}>
                <div style={styles.loading}>Loading...</div>
            </div>
        );
    }

    if (error || !book) {
        return (
            <div style={styles.container}>
                <div style={styles.error}>{error || "Book not found"}</div>
                <button onClick={() => navigate("/")} style={styles.button}>
                    Back to Home
                </button>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <button onClick={() => navigate("/")} style={styles.backButton}>
                ‚Üê Back
            </button>
            <div style={styles.card}>
                <h1 style={styles.title}>{book.title}</h1>
                <p style={styles.author}>By {book.author}</p>
                <div style={styles.description}>
                    <h3>Description</h3>
                    <p>{book.description || "No description available"}</p>
                </div>
            </div>
        </div>
    );
};

const styles = {
    container: {
        padding: "40px",
        minHeight: "100vh",
        background: "linear-gradient(to right, #fdfbfb, #ebedee)",
    },
    card: {
        maxWidth: "800px",
        margin: "0 auto",
        background: "#fff",
        padding: "40px",
        borderRadius: "16px",
        boxShadow: "0 12px 30px rgba(0, 0, 0, 0.1)",
    },
    title: {
        fontSize: "32px",
        marginBottom: "10px",
        color: "#1f2937",
    },
    author: {
        fontSize: "18px",
        color: "#6b7280",
        marginBottom: "30px",
    },
    description: {
        marginTop: "30px",
    },
    backButton: {
        marginBottom: "20px",
        padding: "10px 20px",
        background: "#4b6cb7",
        color: "#fff",
        border: "none",
        borderRadius: "8px",
        cursor: "pointer",
        fontSize: "16px",
    },
    button: {
        padding: "10px 20px",
        background: "#4b6cb7",
        color: "#fff",
        border: "none",
        borderRadius: "8px",
        cursor: "pointer",
        fontSize: "16px",
        marginTop: "20px",
    },
    loading: {
        textAlign: "center",
        fontSize: "20px",
        padding: "40px",
    },
    error: {
        textAlign: "center",
        fontSize: "18px",
        color: "#ef4444",
        padding: "40px",
    },
};

export default BookDetails;
