import React, { useState } from "react";
import { useAuth } from "../../context/AuthContext";
import { addBook } from "../../api/books";
import { useNavigate } from "react-router-dom";

const AddBook = () => {
    const { user, authHeader } = useAuth();
    const navigate = useNavigate();
    const [book, setBook] = useState({
        title: "",
        description: "",
        image: "",
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const submit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        if (!user || !authHeader) {
            setError("Please login to add books");
            setLoading(false);
            return;
        }

        try {
            // Extract email and password from authHeader
            const base64Credentials = authHeader.replace("Basic ", "");
            const credentials = atob(base64Credentials);
            const [email, password] = credentials.split(":");

            // Get user ID from user object (we need to store it during login)
            const userId = user.id || user.userId;
            if (!userId) {
                setError("User ID not found. Please login again.");
                setLoading(false);
                return;
            }

            await addBook(userId, book, email, password);
            navigate("/admin");
        } catch (err) {
            setError("Failed to add book. Please try again.");
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <h2 style={styles.heading}>Add New Book</h2>
                {error && <div style={styles.error}>{error}</div>}
                <form onSubmit={submit} style={styles.form}>
                    <input
                        placeholder="Title"
                        value={book.title}
                        onChange={e => setBook({...book, title:e.target.value})}
                        required
                        style={styles.input}
                    />
                    <input
                        placeholder="Author"
                        value={book.author}
                        onChange={e => setBook({...book, author:e.target.value})}
                        required
                        style={styles.input}
                    />
                    <textarea
                        placeholder="Description"
                        value={book.description}
                        onChange={e => setBook({...book, description:e.target.value})}
                        rows="5"
                        style={styles.textarea}
                    />
                    <button type="submit" disabled={loading} style={styles.button}>
                        {loading ? "Adding..." : "Add Book"}
                    </button>
                </form>
            </div>
        </div>
    );
};

const styles = {
    container: {
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
        padding: "20px",
    },
    card: {
        background: "#fff",
        padding: "40px",
        borderRadius: "16px",
        width: "100%",
        maxWidth: "500px",
        boxShadow: "0 20px 40px rgba(0, 0, 0, 0.2)",
    },
    heading: {
        textAlign: "center",
        marginBottom: "30px",
        color: "#1f2937",
    },
    form: {
        display: "flex",
        flexDirection: "column",
        gap: "15px",
    },
    input: {
        padding: "12px",
        border: "2px solid #e5e7eb",
        borderRadius: "8px",
        fontSize: "16px",
    },
    textarea: {
        padding: "12px",
        border: "2px solid #e5e7eb",
        borderRadius: "8px",
        fontSize: "16px",
        fontFamily: "inherit",
    },
    button: {
        padding: "12px",
        background: "#667eea",
        color: "#fff",
        border: "none",
        borderRadius: "8px",
        fontSize: "16px",
        fontWeight: "600",
        cursor: "pointer",
    },
    error: {
        background: "#fee2e2",
        color: "#dc2626",
        padding: "12px",
        borderRadius: "8px",
        marginBottom: "20px",
        textAlign: "center",
    },
};

export default AddBook;
