import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../../context/AuthContext";
import { getBookById, editBook } from "../../api/books";

const EditBook = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { user, authHeader } = useAuth();
    const [book, setBook] = useState({
        title: "",
        author: "",
        description: "",
    });
    const [loading, setLoading] = useState(false);
    const [fetching, setFetching] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchBook = async () => {
            if (!user || !authHeader) {
                setError("Please login to edit books");
                setFetching(false);
                return;
            }

            try {
                const base64Credentials = authHeader.replace("Basic ", "");
                const credentials = atob(base64Credentials);
                const [email, password] = credentials.split(":");

                const data = await getBookById(id, email, password);
                setBook(data);
            } catch (err) {
                setError("Failed to load book");
                console.error(err);
            } finally {
                setFetching(false);
            }
        };

        fetchBook();
    }, [id, user, authHeader]);

    const submit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        if (!user || !authHeader) {
            setError("Please login to edit books");
            setLoading(false);
            return;
        }

        try {
            const base64Credentials = authHeader.replace("Basic ", "");
            const credentials = atob(base64Credentials);
            const [email, password] = credentials.split(":");

            const userId = user.id || user.userId;
            if (!userId) {
                setError("User ID not found. Please login again.");
                setLoading(false);
                return;
            }

            await editBook(id, userId, book, email, password);
            navigate("/admin");
        } catch (err) {
            setError("Failed to update book. Please try again.");
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    if (fetching) {
        return (
            <div style={styles.container}>
                <div style={styles.loading}>Loading book details...</div>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <h2 style={styles.heading}>Edit Book</h2>
                {error && <div style={styles.error}>{error}</div>}
                <form onSubmit={submit} style={styles.form}>
                    <input
                        value={book.title || ""}
                        onChange={e => setBook({...book, title:e.target.value})}
                        placeholder="Title"
                        required
                        style={styles.input}
                    />
                    <input
                        value={book.author || ""}
                        onChange={e => setBook({...book, author:e.target.value})}
                        placeholder="Author"
                        required
                        style={styles.input}
                    />
                    <textarea
                        value={book.description || ""}
                        onChange={e => setBook({...book, description:e.target.value})}
                        placeholder="Description"
                        rows="5"
                        style={styles.textarea}
                    />
                    <button type="submit" disabled={loading} style={styles.button}>
                        {loading ? "Updating..." : "Update Book"}
                    </button>
                </form>
            </div>
        </div>
    );
};

const styles = {
    container: {
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
        padding: "20px",
    },
    card: {
        background: "#fff",
        padding: "40px",
        borderRadius: "16px",
        width: "100%",
        maxWidth: "500px",
        boxShadow: "0 20px 40px rgba(0, 0, 0, 0.2)",
    },
    heading: {
        textAlign: "center",
        marginBottom: "30px",
        color: "#1f2937",
    },
    form: {
        display: "flex",
        flexDirection: "column",
        gap: "15px",
    },
    input: {
        padding: "12px",
        border: "2px solid #e5e7eb",
        borderRadius: "8px",
        fontSize: "16px",
    },
    textarea: {
        padding: "12px",
        border: "2px solid #e5e7eb",
        borderRadius: "8px",
        fontSize: "16px",
        fontFamily: "inherit",
    },
    button: {
        padding: "12px",
        background: "#667eea",
        color: "#fff",
        border: "none",
        borderRadius: "8px",
        fontSize: "16px",
        fontWeight: "600",
        cursor: "pointer",
    },
    error: {
        background: "#fee2e2",
        color: "#dc2626",
        padding: "12px",
        borderRadius: "8px",
        marginBottom: "20px",
        textAlign: "center",
    },
    loading: {
        textAlign: "center",
        fontSize: "20px",
        padding: "40px",
        color: "#fff",
    },
};

export default EditBook;
